from flask import Blueprint, jsonifyfrom base import app, dbfrom lc_core import get_formated_data_from_db, find_datum, delete_from_db, write_logfrom models import Post@app.route('/api/articles', methods=["GET"])def get_api_articles():        """ Sends all the article inside the database"""        articles = get_formated_data_from_db(db, Post)        return articles, 200@app.route('/api/edit-article/<id>', methods=["DELETE"])def delete_api_article(id):        """ Deletes an article from the database using an id"""        article = find_datum(id=id, dbs=db, model=Post)        try:        delete_from_db(db, article)                return jsonify({            'message': 'Deleted',                        'code': 201        })            except Exception as e:                write_log(e)                return jsonify({            'message': "An error occured",                        'code': 404        })    